import React, { useState, useRef, useEffect } from "react";
import Transition from "../utils/Transition.js";

function FeaturesHome() {
  const [tab, setTab] = useState(1);

  const tabs = useRef(null);

  const heightFix = () => {
    if (tabs.current.children[tab]) {
      tabs.current.style.height =
        tabs.current.children[tab - 1].offsetHeight + "px";
    }
  };

  useEffect(() => {
    heightFix();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [tab]);

  return (
    <section className='relative'>
      {/* Section background (needs .relative class on parent and next sibling elements) */}
      <div
        className='absolute inset-0 bg-gray-100 pointer-events-none mb-16'
        aria-hidden='true'
      ></div>
      <div className='absolute left-0 right-0 m-auto w-px p-px h-20 bg-gray-200 transform -translate-y-1/2'></div>

      <div className='relative max-w-6xl mx-auto px-4 sm:px-6'>
        <div className='pt-12 md:pt-20'>
          {/* Section header */}
          <div className='max-w-3xl mx-auto text-center pb-12 md:pb-16'>
            <h1 className='h2 mb-4'>How Simple works</h1>
            <p className='text-xl text-gray-600'>
              Duis aute irure dolor in reprehenderit in voluptate velit esse
              cillum dolore eu fugiat nulla pariatur excepteur sint occaecat
              cupidatat.
            </p>
          </div>

          {/* Tabs items */}
          <div
            className='max-w-xl md:max-w-none md:w-full mx-auto md:col-span-5 lg:col-span-6 mb-8 md:mb-0 md:order-1'
            data-aos='zoom-y-out'
            ref={tabs}
          >
            <div className='relative flex flex-col text-center lg:text-right'>
              {/* Item 1 */}
              <Transition
                show={tab === 1}
                appear={true}
                className='w-full'
                enter='transition ease-in-out duration-700 transform order-first'
                enterStart='opacity-0 translate-y-16'
                enterEnd='opacity-100 translate-y-0'
                leave='transition ease-in-out duration-300 transform absolute'
                leaveStart='opacity-100 translate-y-0'
                leaveEnd='opacity-0 -translate-y-16'
              >
                <div className='relative inline-flex flex-col'>
                  <img
                    className='md:max-w-none mx-auto rounded'
                    src={require("../images/features-home-bg-01.png").default}
                    width='500'
                    height='375'
                    alt='Features bg'
                  />
                  <img
                    className='md:max-w-none absolute w-full left-0 transform animate-float'
                    src={
                      require("../images/features-home-element-01.png").default
                    }
                    width='500'
                    height='147'
                    alt='Element 01'
                    style={{ top: "22%" }}
                  />
                  <img
                    className='md:max-w-none absolute w-full left-0 transform animate-float animation-delay-500'
                    src={
                      require("../images/features-home-element-02.png").default
                    }
                    width='500'
                    height='158'
                    alt='Element 02'
                    style={{ top: "39%" }}
                  />
                  <img
                    className='md:max-w-none absolute w-full left-0 bottom-0 transform animate-float animation-delay-1000'
                    src={
                      require("../images/features-home-element-03.png").default
                    }
                    width='500'
                    height='167'
                    alt='Element 03'
                    style={{ top: "77%" }}
                  />
                </div>
              </Transition>
              {/* Item 2 */}
              <Transition
                show={tab === 2}
                appear={true}
                className='w-full'
                enter='transition ease-in-out duration-700 transform order-first'
                enterStart='opacity-0 translate-y-16'
                enterEnd='opacity-100 translate-y-0'
                leave='transition ease-in-out duration-300 transform absolute'
                leaveStart='opacity-100 translate-y-0'
                leaveEnd='opacity-0 -translate-y-16'
              >
                <div className='relative inline-flex flex-col'>
                  <img
                    className='md:max-w-none mx-auto rounded'
                    src={require("../images/features-home-bg-01.png").default}
                    width='500'
                    height='375'
                    alt='Features bg'
                  />
                  <img
                    className='md:max-w-none absolute w-full left-0 bottom-0 transform animate-float animation-delay-1000'
                    src={
                      require("../images/features-home-element-03.png").default
                    }
                    width='500'
                    height='167'
                    alt='Element 03'
                    style={{ top: "18%" }}
                  />
                  <img
                    className='md:max-w-none absolute w-full left-0 transform animate-float animation-delay-500'
                    src={
                      require("../images/features-home-element-02.png").default
                    }
                    width='500'
                    height='158'
                    alt='Element 02'
                    style={{ top: "40%" }}
                  />
                  <img
                    className='md:max-w-none absolute w-full left-0 transform animate-float'
                    src={
                      require("../images/features-home-element-01.png").default
                    }
                    width='500'
                    height='147'
                    alt='Element 01'
                    style={{ top: "79%" }}
                  />
                </div>
              </Transition>
              {/* Item 3 */}
              <Transition
                show={tab === 3}
                appear={true}
                className='w-full'
                enter='transition ease-in-out duration-700 transform order-first'
                enterStart='opacity-0 translate-y-16'
                enterEnd='opacity-100 translate-y-0'
                leave='transition ease-in-out duration-300 transform absolute'
                leaveStart='opacity-100 translate-y-0'
                leaveEnd='opacity-0 -translate-y-16'
              >
                <div className='relative inline-flex flex-col'>
                  <img
                    className='md:max-w-none mx-auto rounded'
                    src={require("../images/features-home-bg-01.png").default}
                    width='500'
                    height='375'
                    alt='Features bg'
                  />
                  <img
                    className='md:max-w-none absolute w-full left-0 transform animate-float'
                    src={
                      require("../images/features-home-element-01.png").default
                    }
                    width='500'
                    height='147'
                    alt='Element 01'
                    style={{ top: "22%" }}
                  />
                  <img
                    className='md:max-w-none absolute w-full left-0 transform animate-float animation-delay-500'
                    src={
                      require("../images/features-home-element-02.png").default
                    }
                    width='500'
                    height='158'
                    alt='Element 02'
                    style={{ top: "39%" }}
                  />
                  <img
                    className='md:max-w-none absolute w-full left-0 bottom-0 transform animate-float animation-delay-1000'
                    src={
                      require("../images/features-home-element-03.png").default
                    }
                    width='500'
                    height='167'
                    alt='Element 03'
                    style={{ top: "77%" }}
                  />
                </div>
              </Transition>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

export default FeaturesHome;
